import React from 'react'

const Login = () => {

    const [email, setEmail] = React.useState('')
    const [pass, setPass] = React.useState('')
    const [error, setError] = React.useState(null)

    const procesarDatos = e => {
        e.preventDefault()
        if(!email.trim()){
            //console.log('Ingrese email')
            return
        }
        if(!pass.trim()){
            // console.log('Ingrese Password')
            setError('Ingrese Password')
            return
        }
        if(pass.length < 6){
            // console.log('Password mayor a 6 caracteres')
            setError('Password mayor a 6 caracteres')
            return
        }
        console.log('pasando todas las validaciones!')
    }

    return(
        <div className='mt-5'>
           <h3 className="text-center">acceso o registro de usuarios</h3>
           <hr/>
           <div className="row justify-content-center">
              <div className="col-12 col-sm-8 col-md-6 col-xl-4">
                <form onSubmit={procesarDatos}>
                    {
                        error && (
                            <div className="alert alert-danger">
                                {error}
                            </div>
                        ) 
                    }
                    <input 
                        type="email" 
                        className="form-control mb-2" 
                        placeholder="Ingrse un email"
                        onChange={e => setEmail(e.target.value)}
                        value={email}
                    />
                    <input 
                        type="password" 
                        className="form-control mb-2" 
                        placeholder="Ingrse un password"
                        onChange={e => setPass(e.target.value)}
                        value={pass}
                    />
                    <button className="btn btn-dark btn-lg btn-block">
                        Registrase
                    </button>
                    <button className="btn btn-info btn-sm btn-block">
                        Â¿Ya tienes cuenta?
                    </button>
                </form>
              </div>
           </div>
        </div>
    )
}

export default Login
